<style>
    .navbar_desktop {
        background-color :#141414;
        color : #fff;
        position : fixed;
        width: 100vw;
        z-index: 50;
    }
    .navbar_desktop nav { 
        padding : 15px 0;
    }
    .navbar_desktop nav ul {
        margin :unset;
        list-style-type: none;
    }
    .navbar_desktop nav ul li a {
        display : flex;
        align-items : center;
        justify-content : center;
        text-decoration : none;
        color : #ccc;
    }
    .navbar_desktop nav ul li a:hover {
        color : #fff;
    }
    .nav_trending {
      font-size : 15px!important;
    }
    .navbar_desktop nav ul .searchbar_nav_desktop form {
        width: 350px;
        border-radius : 10px;
        background-color : white;
        display : flex ;
        align-items : center ;
        justify-content : space-between;

    }
    .navbar_desktop nav ul .searchbar_nav_desktop form input {
        border : none;
        background-color : transparent;
        width: 85%;
        padding-left : 15px;
        padding-top :5px;
        padding-bottom : 5px;
    }
    .navbar_desktop nav ul .searchbar_nav_desktop form input:focus-visible {
        outline : none;
    }
    .navbar_desktop nav ul .searchbar_nav_desktop form button {
        border : none;
        background-color : transparent;

    }
</style>